sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if ((isLoading && !g_form.isNewRecord()) || newValue === '' || oldValue == newValue) {
      return;
   }

	var ga = new GlideAjax('GetEmailAddress');
	ga.addParam('sysparm_name','getemail');
	ga.addParam('sysparm_userid',g_form.getValue('u_requested_for'));
	ga.getXML(returnedvalue);

	function returnedvalue(response) {
		var returnedemail = response.responseXML.documentElement.getAttribute('answer');
		g_form.setValue('requested_for_email',returnedemail);
		alert(returnedemail);
	}

   //Type appropriate comment here, and begin script below

}
